mapper.select:
		SELECT 
		FROM omni_job_group_pin AS t
        where t.job_group_id = #{jobGroupId}

mapper.insert:
		INSERT INTO omni_job_group_pin (
            job_group_id,
            pin
		) VALUES (
			#{jobGroupId},
			#{pin}
		);

mapper.delete:
DELETE
FROM omni_job_group_pin t
WHERE t.job_group_id = #{jobGroupId} and t.pin=#{pin}

DELETE
FROM omni_job_group_pin t
WHERE t.job_group_id = #{groupId}
